package com.itssvkv.promap.ui.map.adaptersimport android.app.Activityimport android.content.Contextimport android.view.Viewimport android.widget.TextViewimport com.google.android.gms.maps.GoogleMap.InfoWindowAdapterimport com.google.android.gms.maps.model.Markerimport com.itssvkv.promap.Rimport com.itssvkv.promap.models.LocationDataclass CustomInfoAdapter(private val context: Context) : InfoWindowAdapter {    private var data: LocationData? = null    private val view = (context as Activity).layoutInflater.inflate(R.layout.custom_info_window, null)    override fun getInfoContents(marker: Marker): View? {        data?.let { setupViews(it) }        return view    }    override fun getInfoWindow(marker: Marker): View? {        data?.let { setupViews(it) }        return view    }    fun setData(data: LocationData) {        this.data = data        setupViews(data)    }    private fun setupViews(data: LocationData) {        view.findViewById<TextView>(R.id.countryName).text =            context.getString(R.string.country_name, data.countryName)        view.findViewById<TextView>(R.id.countryCode).text =            context.getString(R.string.country_code, data.countryCode)        view.findViewById<TextView>(R.id.postalCode).text =            context.getString(R.string.postal_code, data.postalCode)        view.findViewById<TextView>(R.id.adminArea).text =            context.getString(R.string.admin_area, data.adminArea)        view.findViewById<TextView>(R.id.lat).text = context.getString(R.string.latitude, data.lat)        view.findViewById<TextView>(R.id.lan).text = context.getString(R.string.longitude, data.lng)    }}