package com.itssvkv.promap.ui.map.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport android.view.ViewGroup.MarginLayoutParamsimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.ListAdapterimport androidx.recyclerview.widget.RecyclerViewimport com.itssvkv.promap.databinding.MapTypeRecyclerBindingimport com.itssvkv.promap.models.MapTypeclass MapTypeAdapter :    ListAdapter<MapType, MapTypeAdapter.ItemViewHolder>(Comparator) {    var onItemSelected: ((MapType) -> Unit)? = null    inner class ItemViewHolder(private val binding: MapTypeRecyclerBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(item: MapType) {            binding.typeNameTv.text = item.name            binding.typeNameTv.setOnClickListener {                onItemSelected?.invoke(item)            }            if (item.id > 1) {                val layoutParams = binding.typeNameTv.layoutParams as MarginLayoutParams                layoutParams.marginStart = 5                binding.typeNameTv.layoutParams = layoutParams            }        }    }    private object Comparator : DiffUtil.ItemCallback<MapType>() {        override fun areItemsTheSame(oldItem: MapType, newItem: MapType) =            oldItem == newItem        override fun areContentsTheSame(oldItem: MapType, newItem: MapType) =            oldItem.id == newItem.id    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemViewHolder {        val binding =            MapTypeRecyclerBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return ItemViewHolder(binding)    }    override fun onBindViewHolder(holder: ItemViewHolder, position: Int) {        val item = getItem(position)        item.let {            holder.bind(it)        }    }}