package com.itssvkv.promap.utilsimport com.google.android.gms.maps.CameraUpdateFactoryimport com.google.android.gms.maps.GoogleMapimport com.google.android.gms.maps.model.CameraPositionobject CameraUtils {    private var cameraTilt = 0f    fun showBuilding(map: GoogleMap?, isChecked: Boolean): Float {        cameraTilt = if (isChecked) 45f else 0f        map?.moveCamera(CameraUpdateFactory.newCameraPosition(            CameraPosition.builder()                .apply {                    target(map.cameraPosition.target)                    tilt(cameraTilt)                    bearing(100f)                    zoom(18f)                }                .build()        ))        return cameraTilt    }    fun setMinZoomLevel(map: GoogleMap?, minLevel: Float = 0f) {        map?.setMinZoomPreference(minLevel)    }    fun setMaxZoomLevel(map: GoogleMap?, maxLevel: Float = 20f) {        map?.setMaxZoomPreference(maxLevel)    }}