package com.itssvkv.promap.utilsimport com.google.android.gms.maps.CameraUpdateFactoryimport com.google.android.gms.maps.GoogleMapimport com.google.android.gms.maps.model.CameraPositionimport com.google.android.gms.maps.model.LatLngimport com.google.android.gms.maps.model.LatLngBoundsobject CameraUtils {    private var cameraTilt = 0f    private val berlin = LatLng(52.51557998966031, 13.406929511438937)    private val nairobi = LatLng(-1.2853552901703755, 36.818578075076495)    private val tokyoBounds = LatLngBounds(        LatLng(35.550676523066294, 139.34723748946033),        LatLng(35.96523735852752, 140.10804068648437)    )    private val denverBounds = LatLngBounds(        LatLng(39.602522363544225, -105.15996599165078),        LatLng(39.91395804122478, -104.5845570710946)    )    fun showBuilding(map: GoogleMap?, isChecked: Boolean): Float {        map?.setLatLngBoundsForCameraTarget(null)        cameraTilt = if (isChecked) 45f else 0f        map?.moveCamera(CameraUpdateFactory.newCameraPosition(            CameraPosition.builder()                .apply {                    target(map.cameraPosition.target)                    tilt(cameraTilt)                    bearing(100f)                    zoom(18f)                }                .build()        ))        return cameraTilt    }    fun moveCameraToBerlin(map: GoogleMap?) {        map?.setLatLngBoundsForCameraTarget(null)        map?.moveCamera(CameraUpdateFactory.newCameraPosition(CameraPosition            .builder()            .apply {                target(berlin)                tilt(cameraTilt)                zoom(10f)                bearing(100f)            }            .build()        )        )    }    fun animateToNairobi(map: GoogleMap?) {        map?.setLatLngBoundsForCameraTarget(null)        map?.animateCamera(CameraUpdateFactory.newCameraPosition(CameraPosition            .builder()            .apply            {                tilt(cameraTilt)                zoom(12f)                target(nairobi)            }            .build()        ), 2000, null)    }    fun moveCameraToTokyoBounds(map: GoogleMap?) {        map?.setLatLngBoundsForCameraTarget(null)        map?.animateCamera(            CameraUpdateFactory.newLatLngBounds(tokyoBounds, 10), 2000, null        )    }    fun moveCameraToDenverBoundsAndRestrict(map: GoogleMap?) {        map?.setLatLngBoundsForCameraTarget(null)        map?.animateCamera(            CameraUpdateFactory.newLatLngBounds(denverBounds, 40)            , 2000, null        )        map?.setLatLngBoundsForCameraTarget(denverBounds)    }    fun setMinZoomLevel(map: GoogleMap?, minLevel: Float = 0f) {        map?.setMinZoomPreference(minLevel)    }    fun setMaxZoomLevel(map: GoogleMap?, maxLevel: Float = 20f) {        map?.setMaxZoomPreference(maxLevel)    }}